          xf.p.x = (1.0 - beta) * this.c0.x + beta * this.c.x;
          xf.p.y = (1.0 - beta) * this.c0.y + beta * this.c.y;
          const angle = (1.0 - beta) * this.a0 + beta * this.a;
          xf.q.SetAngle(angle);
          xf.p.SelfSub(b2Rot.MulRV(xf.q, this.localCenter, b2Vec2.s_t0));
