          if (this.m_needsUpdateAllParticleFlags) {
              this.UpdateAllParticleFlags();
          }
          if (this.m_needsUpdateAllGroupFlags) {
              this.UpdateAllGroupFlags();
          }
          if (this.m_paused) {
              return;
          }
